pub fn load_book(){
    unsafe {
        let mut data = HashMap::new();
        let mut board = Board::new();
        for d in BOOK.iter() {
            if d == &255 {
                board = Board::new();
                continue;
            }
            let p = Pos(1 << d);
            data.insert((board.me, board.opp), p);
            board = board.put(p);
        }
        std::mem::swap(&mut data, &mut KILLER);
        std::mem::forget(data);
        eprintln!("book loaded {} moves", KILLER.len()); //python:debug
    }
}